using System; namespace One_Line { class Program { static void Main(string[] args) { OneLiner[] oneLine = new OneLiner[2]; oneLine[0] = new OneLiner(0); oneLine[1] = new OneLiner(1); for (int i = 0; i < oneLine.Length; i++) { oneLine[i].RevealTheLine(); } } } public class OneLiner { private string theLine; public OneLiner(int i) { if (i < 1) theLine = "This is such an epic line :sob: \t"; if (i == 1) theLine = "Why is there only one line bruh :skull: \t"; } public void RevealTheLine() { Console.Write(theLine); } } }
